import React from 'react'
import { gql } from 'apollo-boost'
import { useQuery } from '@apollo/react-hooks'
import qs from 'querystring'
import Comments from './components/comments'
import './style.scss'
import { Avatar, Image, Spin } from 'antd'
import { SyncOutlined } from '@ant-design/icons'
import dayjs from 'dayjs'
import ReactMarkdown from 'react-markdown'

export const QUERY_SPECIFIC_POST = gql`
    query querySpecificPost($id:ID!){
            post(id: $id){
                 _id title content images isPublic createdAt
                author{
                    email
                    avatar
                    role
                    name
                } 
                likes {
                    avatar
                }
                comments {
                    _id
                    author {
                        name
                        avatar
                    }
                    type
                    content
                    createdAt
                    replies {
                        _id
                        author {
                            name
                        }
                        content
                        createdAt
                    }
                }
                category {
                    label
                    value
                }
            }
    }
`

const PostDetailPage = () => {
  const { id } = qs.parse(location.search.substr(1))
  const { data, loading } = useQuery(QUERY_SPECIFIC_POST, {
    variables: { id }
  })
  const ColorList = ['#f56a00', '#7265e6', '#ffbf00', '#00a2ae']

  const getColor = (name:string) => {
    return (name.charCodeAt(0) - 'A'.charCodeAt(0)) % 4
  }
  if (loading) {
    return <Spin spinning={loading} indicator={<SyncOutlined spin/>}/>
  }
  if (data) {
    return <div className={'post-detail'}>
      <div className={'author'}>
        <Avatar
          style={{
            background: ColorList[getColor(data?.post?.author?.name?.substr(0, 1).toUpperCase())]
          }}
          src={data?.post?.author?.avatar}
          size={48}
        >
          {data.post.author.name.substr(0, 1).toUpperCase()}
        </Avatar>
        <div className={'author-right'}>
          <span className={'author-name'}>
            {data.post.author.name}
          </span>
          <span className={'create-time'}>{ dayjs(Number(data.post.createdAt)).format('YYYY年MM月DD日 HH:MM')}</span>
        </div>

      </div>
      <div className={'content'}>
        <h1 style={{ padding: '10px 0' }}>{data.post.title}</h1>
        <ReactMarkdown>{data.post.content}</ReactMarkdown>
        {
          data.post.images.map((item:string) => <Image
            width={200}
            height={200}
            key={item}
            src={item || 'error'}
            fallback="data:image/png;base64,"
          />)
        }
      </div>
      <Comments comments={data.post.comments} id={id as string}/>
    </div>
  }
  return null
}

export default PostDetailPage
